import './Codelock.css';
import { useState } from 'react';

const Codelock = () => {
    const [buttons, setButtons] = useState([]);
    const [input, setInput] = useState([]);
    const [isOpen, setIsOpen] = useState(false);

    const generateNumPad = () => {
        // if numpad has been already generated
        if (buttons.length > 1) {
            return
        }
        for (let i = 0; i < 10; i++) {
            buttons.push(<div className="button" key={i}><p>{i}</p></div>)
        }
        // Handles moving "0" button to the end of the number pad component (was in the example this way).
        // FYI: array.shift() moves first item in array to be the last item in the array.
        buttons.push(buttons.shift());
    }
    generateNumPad();


    const handleInput = (pressedBtn) => {
        setInput(input + pressedBtn)
        if ((input.length + 1) === 4) {
            checkCode()
            //setInput([])
        }
    }
    const checkCode = () => {
        console.log("Koodi oli: " + input);
    }

    return (
        <div className="codelock-container">
            {
                // How array.map works and more: https://reactjs.org/docs/lists-and-keys.html
                buttons.map((item, id) => {
                    return (
                        <div key={id} onClick={() => handleInput((id + 1))}>
                            {item}
                        </div>
                    )
                })
            }
            <div>{input}</div>
        </div>
    )
}

export default Codelock
