import './Codelock.css';
import { useState } from 'react';
const Codelock = () => {
    const [buttons, setButtons] = useState([]);

    for (let i = 0; i < 10; i++) {
        buttons.push(<div className="button" key={i}><p>{i}</p></div>)
    }

    // Handles moving "0" button to the end of the number pad component.
    // FYI: array.shift() moves first item in array to be the last item in the array.
    buttons.push(buttons.shift());

    return (
        <div className="codelock-container">
            {
                // How array.map works and more: https://reactjs.org/docs/lists-and-keys.html
                buttons.map((item, id) => {
                    return (
                        <div>
                            <div key={id}>{item}</div>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default Codelock
